    def listwidgetclicked(self, item):
       index = self.listWidget_pacientes.row(item)
       self.label_frequencia.setText(str(self.rq[index]['freq']))
       self.label_pressao.setText(str(self.rq[index]['pressao1']) + 'x' + str(self.rq[index]['pressao2']))
       self.label_saturacao.setText(str(self.rq[index]['resp']))
       self.label_temperatura.setText(str(self.rq[index]['temp']))
       self.id = self.rq[index]['cpf']
       print(self.id)

    def sendAlert(self, alert) -> dict:
        return {
            'status': alert,
        }
    
    def estavel(self):
        r = requests.put(
            url=f'{ngrok}/patient/alert/{int(self.id)}', json=self.sendAlert('Estável'))
    
    def alertaEmergencia(self):
        r = requests.put(
            url=f'{ngrok}/patient/alert/{int(self.id)}', json=self.sendAlert('Emergência'))        



