class MyThread(QtCore.QThread):
    ard_signal = QtCore.pyqtSignal(str)
    
    def __init__(self):
        QtCore.QThread.__init__(self)
        
    def run(self):
        while 1:
            ui.listWidget_pacientes.clear()
            resp = ui.listar_pacientes()
            time.sleep(3)
            for p in range(len(resp)):
                self.ard_signal.emit(resp[p]['nome'])

